sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('NewScript');
    ga.addParam('sysparm_name', 'check');
    ga.addParam('sysparm_newValue', newValue);
    ga.getXML(valid);

    function valid(response) {
        var res = response.responseXML.documentElement.getAttribute("res");
        if (res) {
            return true;
        } else {
			g_form.showFieldMsg('fav_place',"Too long input");
            return false;
        }
    }
}
